static inline char *modname_normalize(char *modname, char buf[NAME_MAX],
								size_t *len)
{
	char *c;
	size_t s;

	if (buf) {
		buf[NAME_MAX] = '\0';
		modname = strncpy(buf, modname, NAME_MAX - 1);
	}

